function displayWords(arrayWords) {
	for (i=0; i < arrayWords.length(); i++) {
		// inline comment
		alert("Word " + i + " is " + arrayWords[i]);
	}
}

var tokens = new Array("Hello", "world");
displayWords(tokens);

